import { DefaultSession } from "next-auth";

declare module "next-auth" {
  interface Session {
    user: {
      id: string;
      role: string;
      manufacturerId: string | null;
      manufacturerName: string | null;
    } & DefaultSession["user"];
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    role: string;
    manufacturerId: string | null;
    manufacturerName: string | null;
  }
}
